$filePath = global_common::FOLDER_FILES_OTHER."stores.csv";
$file_handle = fopen($filePath, "r");
$stack = array();
//ignore header
$line_of_text = fgetcsv($file_handle);
while (!feof($file_handle) ) {
	$line_of_text = fgetcsv($file_handle);
	if($line_of_text)
		array_push($stack,$line_of_text);
}
fclose($file_handle);
$count = count($stack);
for($i=0; $i < $count; $i++)
{
	print_r($stack[$i]);
	echo "<br>";
	$stack[$i][4] = 1;
}


$fp = fopen($filePath, 'w');
fputcsv($fp, array('ID','Name','Address','Email','Sent'));
foreach ($stack as $item) {
	fputcsv($fp, $item);
}

fclose($fp);
